pragma solidity 0.8.7;

import "../vendors/ERC20_Cloneable.sol";
import "../interfaces/IPoolToken.sol";

/// @title The pool token; used for ownership/shares of the underlying tokens of the long/short pool
/// @dev ERC_20_Cloneable contains onlyOwner code implemented for use with the cloneable setup
contract PoolToken is ERC20_Cloneable, IPoolToken {
    // #### Global state

    // #### Functions

    constructor(uint8 _decimals) ERC20_Cloneable("BASE_TOKEN", "BASE", _decimals) {}

    /**
     * @notice Mints pool tokens
     * @param amount Pool tokens to burn
     * @param account Account to burn pool tokens to
     * @return Whether the mint was successful
     */
    function mint(uint256 amount, address account) external override onlyOwner returns (bool) {
        _mint(account, amount);
        return true;
    }

    /**
     * @notice Burns pool tokens
     * @param amount Pool tokens to burn
     * @param account Account to burn pool tokens from
     * @return Whether the burn was successful
     */
    function burn(uint256 amount, address account) external override onlyOwner returns (bool) {
        _burn(account, amount);
        return true;
    }
}
","keccak256":"0x2e9097fbbe74654591ae70d0301828a73b33e5aaa5f41048ea74e35dd1112844","license":"CC-BY-NC-ND-4.0"},"contracts/implementation/PriceObserver.sol":{"content":"//SPDX-License-Identifier: CC-BY-NC-ND-4.0
pragma solidity 0.8.7;

import "../interfaces/IPriceObserver.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/// Stores a finite sequence of price observations
contract PriceObserver is Ownable, IPriceObserver {
    /// Maximum number of elements storable by the backing array
    uint256 public constant MAX_NUM_ELEMS = 24;

    /// Current number of elements stored by the backing array
    uint256 public numElems = 0;

    /// Backing array for storing price data
    int256[MAX_NUM_ELEMS] public observations;

    /// Writer -- only address allowed to add data to the backing array
    address writer = address(0);

    /**
     * @notice Returns the capacity of the backing array (i.e., the maximum
     *          number of price observations able to be stored by this contract)
     * @return Maximum number of price observations that can be stored
     * @dev `MAX_NUM_ELEMS`
     */
    function capacity() public pure override returns (uint256) {
        return MAX_NUM_ELEMS;
    }

    /**
     * @notice Returns the current number of price observations stored
     * @return Current number of price observations stored
     * @dev Should always be less than or equal to `capacity`
     * @dev `numElems`
     */
    function length() public view override returns (uint256) {
        return numElems;
    }

    /**
     * @notice Retrieves the `i`th price observation
     * @param i Period to retrieve the price observation of
     * @return `i`th price observation
     * @dev Throws if index is out of bounds (i.e., `i >= length()`)
     */
    function get(uint256 i) public view override returns (int256) {
        require(i < length(), "PO: Out of bounds");
        return observations[i];
    }

    /**
     * @notice Retrieves all price observations
     * @return Backing array of all price observations
     * @dev Note that, due to this view simply returning a reference to the
     *      backing array, it's possible for there to be null prices (i.e., 0)
     */
    function getAll() public view override returns (int256[24] memory) {
        return observations;
    }

    /**
     * @notice Adds a new price observation to the contract
     * @param x Price
     * @return Whether or not an existing price observation was rotated out
     * @dev If the backing array is full (i.e., `length() == capacity()`, then
     *      it is rotated such that the oldest price observation is deleted
     * @dev Only callable by the associated writer for this contract
     */
    function add(int256 x) public override onlyWriter returns (bool) {
        if (full()) {
            leftRotateWithPad(x);
            return true;
        } else {
            observations[length()] = x;
            numElems += 1;
            return false;
        }
    }

    /**
     * @notice Sets the associated writer address for this contract
     * @param _writer Address of the new writer
     * @dev Only callable by the owner of this contract
     * @dev Throws if `_writer` is the null address
     */
    function setWriter(address _writer) public onlyOwner {
        require(_writer != address(0), "PO: Null address not allowed");
        writer = _writer;
    }

    /**
     * @notice Returns the current writer of this contract
     * @return Address of the writer for this contract
     * @dev `writer`
     */
    function getWriter() public view returns (address) {
        return writer;
    }

    /**
     * @notice Determines whether or not the backing array is full
     * @return Flag indicating whether the backing array is full or not
     * @dev `length() == capacity()`
     */
    function full() private view returns (bool) {
        return length() == capacity();
    }

    /**
     * @notice Resets the backing array and clears all of its stored prices
     * @dev Only callable by the owner of this contract
     */
    function clear() public onlyOwner {
        numElems = 0;
        delete observations;
    }

    /**
     * @notice Rotates observations array to the **left** by one element and
     *          sets the last element of `xs` to `x`
     * @param x Element to "rotate into" observations array
     */
    function leftRotateWithPad(int256 x) private {
        uint256 n = length();

        /* linear scan over the [1, n] subsequence */
        for (uint256 i = 1; i < n; i++) {
            observations[i - 1] = observations[i];
        }

        /* rotate `x` into `observations` from the right (remember, we're
         * **left** rotating -- with padding!) */
        observations[n - 1] = x;
    }

    /**
     * @notice Enforces that the caller is the associated writer of this
     *          contract
     */
    modifier onlyWriter() {
        require(msg.sender == writer, "PO: Permission denied");
        _;
    }
}
","keccak256":"0x7ba9d2ccb090627c6186ea5f03feb830633fa69d037dc0970874348f4d93a08f","license":"CC-BY-NC-ND-4.0"},"contracts/interfaces/IAutoClaim.sol":{"content":"//SPDX-License-Identifier: CC-BY-NC-ND-4.0
pragma solidity 0.8.7;

interface IAutoClaim {
    /**
     * @notice Creates a notification when an auto-claim is requested
     * @param user The user who made a request
     * @param poolCommitter The PoolCommitter instance in which the commit was made
     * @param updateIntervalId The update interval ID that the corresponding commitment was allocated for
     * @param reward The reward for the auto-claim
     */
    event PaidClaimRequest(
        address indexed user,
        address indexed poolCommitter,
        uint256 indexed updateIntervalId,
        uint256 reward
    );

    /**
     * @notice Creates a notification when an auto-claim request is updated. i.e. When another commit is added and reward is incremented.
     * @param user The user whose request got updated
     * @param poolCommitter The PoolCommitter instance in which the commits were made
     * @param newReward The new total reward for the auto-claim
     */
    event PaidClaimRequestUpdate(address indexed user, address indexed poolCommitter, uint256 indexed newReward);

    /**
     * @notice Creates a notification when an auto-claim request is executed
     * @param user The user whose request got executed
     * @param poolCommitter The PoolCommitter instance in which the original commit was made
     * @param reward The reward for the auto-claim
     */
    event PaidRequestExecution(address user, address poolCommitter, uint256 reward);

    /**
     * @notice Creates a notification when an auto-claim request is withdrawn
     * @param user The user whose request got withdrawn
     * @param poolCommitter The PoolCommitter instance in which the original commit was made
     */
    event RequestWithdrawn(address user, address poolCommitter);

    struct ClaimRequest {
        uint128 updateIntervalId; // The update interval during which a user requested a claim.
        uint256 reward; // The amount of ETH in wei that was given by the user to pay for upkeep
    }

    /**
     * @notice Pay for your commit to be claimed. This means that a willing participant can claim on `user`'s behalf when the current update interval ends.
     * @dev Only callable by this contract's associated PoolCommitter instance. This prevents griefing. Consider a permissionless function, where a user can claim that somebody else wants to auto claim when they do not.
     * @param user The user who wants to autoclaim.
     */
    function makePaidClaimRequest(address user) external payable;

    /**
     * @notice Claim on the behalf of a user who has requests to have their commit automatically claimed by a keeper.
     * @param user The user who requested an autoclaim.
     * @param poolCommitterAddress The PoolCommitter address within which the user's claim will be executed
     */
    function paidClaim(address user, address poolCommitterAddress) external;

    /**
     * @notice Call `paidClaim` for multiple users, across multiple PoolCommitters
     * @param users All users to execute claims for.
     * @param poolCommitterAddresses The PoolCommitter addresses within which you would like to claim for the respective user
     * @dev The nth index in poolCommitterAddresses should be the PoolCommitter where the nth address in user requested an auto claim
     */
    function multiPaidClaimMultiplePoolCommitters(address[] calldata users, address[] calldata poolCommitterAddresses)
        external;

    /**
     * @notice Call `paidClaim` for multiple users, in a single PoolCommitter.
     * @dev The nth index in poolCommitterAddresses should be the PoolCommitter where the nth address in user requested an auto claim
     * @param users All users to execute claims for.
     * @param poolCommitterAddress The PoolCommitter address within which you would like to claim for the respective user
     */
    function multiPaidClaimSinglePoolCommitter(address[] calldata users, address poolCommitterAddress) external;

    /**
     * @notice If a user's claim request never gets executed (due to not high enough of a reward), or they change their minds, enable them to withdraw their request.
     * @param poolCommitter The PoolCommitter for which the user's commit claim is to be withdrawn.
     */
    function withdrawClaimRequest(address poolCommitter) external;

    /**
     * @notice When the user claims themself through poolCommitter, you want the
     * @param user The user who will have their claim request withdrawn.
     */
    function withdrawUserClaimRequest(address user) external;

    /**
     * @notice Check the validity of a user's claim request for a given pool committer.
     * @return true if the claim request can be executed.
     * @param user The user whose claim request will be checked.
     * @param poolCommitter The pool committer in which to look for a user's claim request.
     */
    function checkUserClaim(address user, address poolCommitter) external view returns (bool);

    /**
     * @return true if the given claim request can be executed.
     * @dev A claim request can be executed only if one exists and is from an update interval that has passed.
     * @param request The ClaimRequest object to be checked.
     * @param currentUpdateIntervalId The current update interval. Used to compare to the update interval of the ClaimRequest.
     */
    function checkClaim(ClaimRequest memory request, uint256 currentUpdateIntervalId) external pure returns (bool);
}
