// SPDX-License-Identifier: MIT
pragma solidity ^0.7.6;
pragma abicoder v2;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

/*
@title An ERC20 token to be used when testing a pool
@dev Don't use this for anything real. There's no access controls on mint and burn.
*/
contract TestToken is ERC20 {
    constructor(string memory name, string memory symbol) ERC20(name, symbol) {}

    function mint(uint256 amount, address account) external returns (bool) {
        _mint(account, amount);
        return true;
    }

    function burn(uint256 amount, address account) external returns (bool) {
        _burn(account, amount);
        return true;
    }
}

contracts/test-utilities/IChainlinkOracl.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.7.6;

/**
 * This interface follows the AggregatorV3 interface. The getRoundData has been excluded as only the
 * latestRoundData function is used in the system to fetch answers.
 * https://github.com/smartcontractkit/chainlink/blob/develop/evm-contracts/src/v0.8/interfaces/AggregatorV3Interface.sol
 * Before being used by the system, any Chainlink feeds that do not provide answers in WAD format (18 decimals) should be wrapped in a
 * Tracer Chainlink Adapter (see contrafts/oracle/ChainlinkOracleAdapter.sol) to ensure the correct number of decimals.
 */
interface IChainlinkOracle {
    function decimals() external view returns (uint8);

    function description() external view returns (string memory);

    function version() external view returns (uint256);

    // latestRoundData should raise "No data present"
    // if they do not have data to report, instead of returning unset values
    // which could be misinterpreted as actual reported values.
    function latestRoundData()
        external
        view
        returns (
            uint80 roundId,
            int256 answer,
            uint256 startedAt,
            uint256 updatedAt,
            uint80 answeredInRound
        );
}

contracts/test-utilities/TestChainlinkOr.content:
// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity ^0.7.6;

/**
 * @dev The following is a mock Chainlink Price Feed Implementation.
 *      It is used purely for the purpose of testing.
 *      DO NOT USE IN PRODUCTION
 */
contract TestChainlinkOracle {
    int256 public price = 100000000;
    uint8 public decimals = 8; // default of 8 decimals for USD price feeds in the Chainlink ecosystem
    string public description = "A mock Chainlink V3 Aggregator";
    uint256 public version = 3; // Aggregator V3;
    uint80 private ROUND_ID = 1; // A mock round Id

    /**
     * @notice Returns round data with the set price as the answer.
     *         Other fields are returned as mock data to simulate a
     *         successful round.
     */
    function latestRoundData()
        external
        view
        returns (
            uint80 roundId,
            int256 answer,
            uint256 startedAt,
            uint256 updatedAt,
            uint80 answeredInRound
        )
    {
        roundId = ROUND_ID;
        answer = price;
        startedAt = 0;
        updatedAt = block.timestamp;
        answeredInRound = ROUND_ID;

        return (roundId, answer, startedAt, updatedAt, answeredInRound);
    }

    /**
     * @notice Sets the answer that is returned by the Oracle when latestRoundData is called
     */
    function setPrice(int256 _price) public {
        price = _price;
    }

    /**
     * @notice Sets the decimals returned in the answer
     */
    function setDecimals(uint8 _decimals) external {
        decimals = _decimals;
    }
}

contracts/test-utilities/TestOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.7.6;
pragma abicoder v2;

/*
@title A mockup oracle wrapper. Don't use for production.
*/
contract TestOracle {
    int256 internal price;
    address public oracle;

    function setOracle(address _oracle) external {
        require(oracle != address(0), "Oracle cannot be 0 address");
        oracle = _oracle;
    }

    function incrementPrice() external {
        price += 1;
    }

    function getPrice() external view returns (int256) {
        return price;
    }
}
